#include <gtest/gtest.h>

#include "base64.cpp"


TEST(Encode, SimpleTestOne)
{
  EXPECT_EQ(base64encode("Many hands make light work."),"TWFueSBoYW5kcyBtYWtlIGxpZ2h0IHdvcmsu");
}

TEST(Encode, SimpleTestTwo)
{
  EXPECT_EQ(base64encode("ABCDEFGHIJKLMNOPQRSTUVWXYabcdefghijklmnopqrstuvwxy0123456789"),"QUJDREVGR0hJSktMTU5PUFFSU1RVVldYWWFiY2RlZmdoaWprbG1ub3BxcnN0dXZ3eHkwMTIzNDU2Nzg5");
}

TEST(Encode, SimpleTestThree)
{
  EXPECT_EQ(base64encode("...."), "Li4uLg==");
}

TEST(Encode, BigTest)
{
  EXPECT_EQ(base64encode(""),
  "");
}

TEST(Decode, SimpleTestOne)
{
  EXPECT_EQ(base64decode("TWFueSBoYW5kcyBtYWtlIGxpZ2h0IHdvcmsu"),"Many hands make light work.");
}

TEST(Decode, SimpleTestTwo)
{
  EXPECT_EQ(base64decode("QUJDREVGR0hJSktMTU5PUFFSU1RVVldYWWFiY2RlZmdoaWprbG1ub3BxcnN0dXZ3eHkwMTIzNDU2Nzg5"),"ABCDEFGHIJKLMNOPQRSTUVWXYabcdefghijklmnopqrstuvwxy0123456789");
}

TEST(Decode, SimpleTestThree)
{
  EXPECT_EQ(base64decode("Li4uLg=="), "....");
}

